import { USER_REGISTER_FAIL, USER_REGISTER_REQUEST, USER_REGISTER_SUCCESS, USER_SIGNIN_FAIL, USER_SIGNIN_REQUEST, USER_SIGNIN_SUCCESS, USER_UPDATE_FAIL, USER_UPDATE_REQUEST, USER_UPDATE_SUCCESS } from "../constants/userConstants";

//Reducers are functions that take the current state and an action as arguments, and return a new state result. 
function userSigninReducer(state={},action){
    switch(action.type){
        case USER_SIGNIN_REQUEST:
            return {loading:true};
        case USER_SIGNIN_SUCCESS:
            return {loading:false, userInfo: action.payload};
        case USER_SIGNIN_FAIL:
            return {loading:false, error:action.payload};
        default:
            return state; 
    }
}

function userRegisterReducer(state={},action){
    switch(action.type){
        case USER_REGISTER_REQUEST:
            return {loading:true};
        case USER_REGISTER_SUCCESS:
            return {loading:false, userInfo: action.payload};
        case USER_REGISTER_FAIL:
            return {loading:false, error:action.payload};
        case USER_UPDATE_REQUEST:
            return {loading:true};
        case USER_UPDATE_SUCCESS:
            return {loading:false, userInfo: action.payload};
        case USER_UPDATE_FAIL:
            return {loading:false, error:action.payload};
        default:
            return state; 
    }
}

export {userSigninReducer,userRegisterReducer}
